import pandas as pddef code_mapping(df, src, targ):    """ Map labels in src and targ columns to integers """        # Sort source and target value labels    labels = list(df[src]) + list(df[targ])    labels = sorted(list(set(labels)))    #print(labels)        # Find number of labels in total for source and target values    codes = list(range(len(labels)))    #print(codes)        # Assign number values to source and target labels in dict object    lcmap = dict(zip(labels, codes))    #print(lcmap)        # Replace source and target value labels in DataFrame with corresponding number values    df = df.replace({src: lcmap, targ: lcmap})    #print(df)        return df, labels